package firstjava;

public class switchex {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int rollno = 21;
		
		switch(rollno) {
		case 55:
		System.out.println("Student name is Rahul");
		System.out.println("Student class is Computer");
		break;
		case 35:
		System.out.println("Student name is Pratap");
		System.out.println("Student class is Science");
		break;
		case 60:
		System.out.println("Student name is Prativa");
		System.out.println("Student class is English");
		break;
		
		default:
		System.out.println("Student information not available"); 
		
		}
		

	}

}
